using ll = long long;
#define f first
#define s second
#define pb push_back
#define all(v) v.begin(), v.end()

const int NAX = 2e5 + 5, MOD = 1000000007;

class Solution
{
public:
    int numWays(string s)
    {
        int cnt = count(all(s), '1');
        db(cnt);
        int n = s.size();
        if (cnt == 0)
        {
            return n * (n - 1) * (n - 2) / 6;
        }
        if (cnt % 3 == 0)
        {
            cnt /= 3;
            int cnt1 = 0;
            int ans = 1;
            for (size_t i = 0; i < n; i++)
            {
                cnt1 += s[i] - '0';
                if (cnt1 == cnt)
                {
                    for (size_t j = i + 1; j < n; j++)
                    {
                        if (s[j] == '1')
                        {
                            ans *= j - i;
                            break;
                        }
                    }
                    break;
                }
            }
            reverse(all(s));
            cnt1 = 0;
            for (size_t i = 0; i < n; i++)
            {
                cnt1 += s[i] - '0';
                if (cnt1 == cnt)
                {
                    for (size_t j = i + 1; j < n; j++)
                    {
                        if (s[j] == '1')
                        {
                            ans *= j - i;
                            break;
                        }
                    }
                    break;
                }
            }
            return ans;
        }
        return 0;
    }
};

